import { Component, OnInit, OnDestroy } from '@angular/core';
import { Subscription } from 'rxjs';
import { Router } from '@angular/router';

import { Ingredient } from '../shared/ingredient.model';
import { ShoppingListService } from './shopping-list.service';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { ShopList } from '../shoplist.model';
import { map } from 'rxjs';
import { EmitterService } from '../emitter.service';


@Component({
  selector: 'app-shopping-list',
  templateUrl: './shopping-list.component.html',
  styleUrls: ['./shopping-list.component.css']
})
export class ShoppingListComponent implements OnInit, OnDestroy {
  ingredients: Ingredient[];
  private subscription: Subscription;
  loadedPosts = [];
  shoppingList: ShopList[] = [];
  data: ShopList;
  isUserLoggedIn: boolean = false;

  constructor(private slService: ShoppingListService,
    private http: HttpClient,
    private emitService: EmitterService,
    private router: Router) { }

  ngOnInit() {
    this.ingredients = this.slService.getIngredients();
    this.subscription = this.slService.ingredientsChanged
      .subscribe(
        (ingredients: Ingredient[]) => {
          this.ingredients = ingredients;
        }
      );
    this.onFetchPosts()
    this.isUserLoggedIn = this.checkLoggedIn();
    console.log("haha" + this.isUserLoggedIn);
  }

  onEditItem(index: number) {
    this.slService.startedEditing.next(index);
  }

  checkLoggedIn() {
    let temp = localStorage.getItem('token');
    if (temp == null){
      return false;
    }
    return true;
  }

  onLogout() {
    const token = localStorage.getItem('token');
    const headers = new HttpHeaders({
      'Authorization': "Bearer eyJhbGciOiJIUzUxMiJ9.eyJyb2xlIjpbIkFETUlOIiwiQ1VTVE9NRVIiXSwidXNlck5hbWUiOiJ1c2VybmFtZSIsInVzZXJJZCI6IjU1YWZmNmYwLTMxMWYtMTFlZS04Nzg2LWM4NWFjZjA2ODk4ZSIsInN1YiI6IjU1YWZmNmYwLTMxMWYtMTFlZS04Nzg2LWM4NWFjZjA2ODk4ZSIsImlhdCI6MTY5MTA0OTI4MywiZXhwIjoxNjkxNjU0MDgzfQ.zRmupTR7dxDO5a5EhTLDZqwhinEkDfb2RYqFxrdXDank94AbnUneEnaU4af0Pbdtens6q1nf53Ums9lajMCP6g"
    })
    console.log(headers);
    this.http.post(
      'http://localhost:8080/api/auth/v1/logout',
      {headers: headers }
    ).subscribe(data => {
      console.log(data);
      localStorage.removeItem('token');
      location.reload();
    })

  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

  onFetchPosts() {
    // Send Http request
    let headers = new HttpHeaders({});
    this.http.get(
      'http://localhost:8080/api/product/v1',
      {
        headers: headers.set('Authorization', 'Bearer eyJhbGciOiJIUzUxMiJ9.eyJyb2xlIjpbIkNVU1RPTUVSIl0sInVzZXJOYW1lIjoidGVzdDExMSIsInVzZXJJZCI6IjhmMmYwZTE4LTJmNzItMTFlZS04Nzg2LWM4NWFjZjA2ODk4ZSIsInN1YiI6IjhmMmYwZTE4LTJmNzItMTFlZS04Nzg2LWM4NWFjZjA2ODk4ZSIsImlhdCI6MTY5MDc4ODczNiwiZXhwIjoxNjkxMzkzNTM2fQ.K0Vnj1cWjdMEddn6nyO6m5d_kaHzCcsJW_PgmMc2Jmy9ynpOzZxf3nyEmsVVBqDONfuejzkmljTDFRyM24cJxw')
      }
    ).pipe(map(responseData => {
      const shoppingArray: ShopList[] = [];
      for (const key in responseData){
        if (responseData.hasOwnProperty(key)) {
          shoppingArray.push({ ...responseData[key], id: key });
        }
      }
      console.log(shoppingArray);
      return shoppingArray;
    })).subscribe(responseData => {
      console.log(responseData);
      this.shoppingList = responseData;
      }, error => {
      console.log(error);
    });
  }

  changePassword() {
    this.router.navigate(['/changepassword']);
  }

}
