import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { ShopList } from './shoplist.model';
import { map } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  loadedPosts = [];
  shoppingList: ShopList[] = [];
  data: ShopList;

  constructor(private http: HttpClient) {}

  ngOnInit() {}

  onCreatePost(postData: { title: string; content: string }) {
    // Send Http request
    this.http.post(
      'https://ng-complete-guide-7b384-default-rtdb.asia-southeast1.firebasedatabase.app/posts.json',
      postData
    ).subscribe(responseData => {
      console.log(responseData);
    })
    console.log(postData);
  }

  onFetchPosts() {
    // Send Http request
    let headers = new HttpHeaders({});
    this.http.get(
      'http://localhost:8080/api/product/v1',
      {
        headers: headers.set('Authorization', 'Bearer eyJhbGciOiJIUzUxMiJ9.eyJyb2xlIjpbIkNVU1RPTUVSIl0sInVzZXJOYW1lIjoidGVzdDExMSIsInVzZXJJZCI6IjhmMmYwZTE4LTJmNzItMTFlZS04Nzg2LWM4NWFjZjA2ODk4ZSIsInN1YiI6IjhmMmYwZTE4LTJmNzItMTFlZS04Nzg2LWM4NWFjZjA2ODk4ZSIsImlhdCI6MTY5MDc4ODczNiwiZXhwIjoxNjkxMzkzNTM2fQ.K0Vnj1cWjdMEddn6nyO6m5d_kaHzCcsJW_PgmMc2Jmy9ynpOzZxf3nyEmsVVBqDONfuejzkmljTDFRyM24cJxw')
      }
    ).pipe(map(responseData => {
      const shoppingArray: ShopList[] = [];
      for (const key in responseData){
        if (responseData.hasOwnProperty(key)) {
          shoppingArray.push({ ...responseData[key], id: key });
        }
      }
      console.log(shoppingArray);
      return shoppingArray;
    })).subscribe(responseData => {
      console.log(responseData);
      this.shoppingList = responseData;
      }, error => {
      console.log(error);
    });
  }

  onClearPosts() {
    // Send Http request
  }

  onRegister(postData: { userName: string; emailAddress: string; password: string }) {
    this.http.post(
      'http://localhost:8080/api/user/v1/register',
      postData
    ).subscribe(responseData => {
      console.log(responseData);
    })
    console.log(postData);
  }

  onLogin(postData: { userName: string; password: string }) {
    this.http.post(
      'http://localhost:8080/api/auth/v1/login',
      postData
    ).subscribe(responseData => {
      console.log(responseData);
    })
    console.log(postData);
  }

}
